var mouseMode = localStorage.getItem("mouse");
function changeMouseMode() {
    if (localStorage.getItem("mouse") == "on") {
        mouseMode = "off";
        localStorage.setItem("mouse", "off");
        debounce(function () {
            new Vue({
                data: function () {
                    this.$notify({
                        title: "åˆ‡æ¢å³é”®æ¨¡å¼æˆåŠŸğŸ”",
                        message: "å½“å‰é¼ æ ‡å³é”®å·²æ¢å¤ä¸ºç³»ç»Ÿé»˜è®¤ï¼",
                        position: 'top-left',
                        offset: 50,
                        showClose: true,
                        type: "success",
                        duration: 5000
                    });
                }
            })
        }, 300);
    } else {
        mouseMode = "on";
        localStorage.setItem("mouse", "on");
        debounce(function () {
            new Vue({
                data: function () {
                    this.$notify({
                        title: "åˆ‡æ¢å³é”®æ¨¡å¼æˆåŠŸğŸ”",
                        message: "å½“å‰é¼ æ ‡å³é”®å·²æ›´æ¢ä¸ºç½‘ç«™æŒ‡å®šæ ·å¼ï¼",
                        position: 'top-left',
                        offset: 50,
                        showClose: true,
                        type: "success",
                        duration: 5000
                    });
                }
            })
        }, 300);
    }
}

setInterval(() => {
  let create_time = Math.round(new Date('2024-03-28 00:00:00').getTime() / 1000); //åœ¨æ­¤è¡Œä¿®æ”¹å»ºç«™æ—¶é—´
  let timestamp = Math.round((new Date().getTime()) / 1000);
  let second = timestamp - create_time;
  let time = new Array(0, 0, 0, 0, 0);

  var nol = function(h){
    return h>9?h:'0'+h;
  }
  if (second >= 365 * 24 * 3600) {
    time[0] = parseInt(second / (365 * 24 * 3600));
    second %= 365 * 24 * 3600;
  }
    if (second >= 30 * 24 * 3600) {
    time[1] = parseInt(second / (30 * 24 * 3600));
    second %= 30 * 24 * 3600;
  }
  if (second >= 24 * 3600) {
    time[2] = parseInt(second / (24 * 3600));
    second %= 24 * 3600;
  }
  if (second >= 3600) {
    time[3] = nol(parseInt(second / 3600));
    second %= 3600;
  }
  if (second >= 60) {
    time[4] = nol(parseInt(second / 60));
    second %= 60;
  }
  if (second > 0) {
    time[5] = nol(second);
  }
  if ((Number(time[3])<22) && (Number(time[3])>7)){
    currentTimeHtml ="<img class='boardsign' src='https://img.shields.io/badge/å–µå–µå±‹-è¥ä¸šä¸­-6adea8?style=social&logo=cakephp' title='è·ç¦»ç™¾å¹´è€åº—ä¹Ÿå°±å·®ä¸åˆ°ä¸€ç™¾å¹´~'><div id='runtime'>" + time[0] + ' å¹´ ' + time[1] + ' æœˆ ' + time[2] + ' æ—¥ ' + time[3] + ' : ' + time[4] + ' : ' + time[5] + '</div>';
  }
  else{
    currentTimeHtml ="<img class='boardsign' src='https://img.shields.io/badge/å–µå–µå±‹-æ‰“çƒŠäº†-6adea8?style=social&logo=coffeescript' title='è¿™ä¸ªç‚¹äº†åº”è¯¥å»ç¡è§‰å•¦ï¼Œç†¬å¤œå¯¹èº«ä½“ä¸å¥½å“¦'><div id='runtime'>" + time[0] + ' å¹´ ' + time[1] + ' æœˆ ' + time[2] + ' æ—¥ ' + time[3] + ' : ' + time[4] + ' : ' + time[5] + '</div>';
  }
  document.getElementById("workboard").innerHTML = currentTimeHtml;
}, 1000);

