    else document.documentElement.requestFullscreen();
}

// 右键开关
if (localStorage.getItem("mouse") == undefined) {
    localStorage.setItem("mouse", "on");
}
var mouseMode = localStorage.getItem("mouse");
function changeMouseMode() {
    if (localStorage.getItem("mouse") == "on") {
        mouseMode = "off";
        localStorage.setItem("mouse", "off");
        debounce(function () {
            new Vue({
                data: function () {
                    this.$notify({
                        title: "切换右键模式成功🍔",
                        message: "当前鼠标右键已恢复为系统默认！",
                        position: 'top-left',
                        offset: 50,
                        showClose: true,
                        type: "success",
                        duration: 5000
                    });
                }
            })
        }, 300);
    } else {
        mouseMode = "on";
        localStorage.setItem("mouse", "on");
        debounce(function () {
            new Vue({
                data: function () {
                    this.$notify({
                        title: "切换右键模式成功🍔",
                        message: "当前鼠标右键已更换为网站指定样式！",
                        position: 'top-left',
                        offset: 50,
                        showClose: true,
                        type: "success",
                        duration: 5000
                    });
                }
            })
        }, 300);
    }
}

setInterval(() => {
  let create_time = Math.round(new Date('2024-03-28 00:00:00').getTime() / 1000); //在此行修改建站时间
  let timestamp = Math.round((new Date().getTime()) / 1000);
  let second = timestamp - create_time;
  let time = new Array(0, 0, 0, 0, 0);

  var nol = function(h){
    return h>9?h:'0'+h;
  }
  if (second >= 365 * 24 * 3600) {
    time[0] = parseInt(second / (365 * 24 * 3600));
    second %= 365 * 24 * 3600;
  }
    if (second >= 30 * 24 * 3600) {
    time[1] = parseInt(second / (30 * 24 * 3600));
    second %= 30 * 24 * 3600;
  }
  if (second >= 24 * 3600) {
    time[2] = parseInt(second / (24 * 3600));
    second %= 24 * 3600;
  }
  if (second >= 3600) {
    time[3] = nol(parseInt(second / 3600));
    second %= 3600;
  }
  if (second >= 60) {
    time[4] = nol(parseInt(second / 60));
    second %= 60;
  }
  if (second > 0) {
    time[5] = nol(second);
  }
  if ((Number(time[3])<22) && (Number(time[3])>7)){
    currentTimeHtml ="<img class='boardsign' src='https://img.shields.io/badge/喵喵屋-营业中-6adea8?style=social&logo=cakephp' title='距离百年老店也就差不到一百年~'><div id='runtime'>" + time[0] + ' 年 ' + time[1] + ' 月 ' + time[2] + ' 日 ' + time[3] + ' : ' + time[4] + ' : ' + time[5] + '</div>';
  }
  else{
    currentTimeHtml ="<img class='boardsign' src='https://img.shields.io/badge/喵喵屋-打烊了-6adea8?style=social&logo=coffeescript' title='这个点了应该去睡觉啦，熬夜对身体不好哦'><div id='runtime'>" + time[0] + ' 年 ' + time[1] + ' 月 ' + time[2] + ' 日 ' + time[3] + ' : ' + time[4] + ' : ' + time[5] + '</div>';
  }
  document.getElementById("workboard").innerHTML = currentTimeHtml;
}, 1000);

